    <el-row class='leaderboard'>

      <el-col v-if="loaded">
        <h2>Leaderboard</h2>
        <!--Leaderboard here-->
        <el-table :data="buildings" :stripe="true" style="width: 100%" :border="true">
          <el-table-column prop="rank" label="Rank" width="80" />
          <el-table-column prop="name" label="Building" width="200"/>
          <el-table-column prop="percent" label="% Power Change"/>
          <el-table-column prop="current" label="Current Accumulative kwh"/>
          <el-table-column prop="baseline" label="Baseline kwh"/>
        </el-table>
      </el-col>
      <el-col v-if="!loaded">
        <h2>Loading... {{ status }}</h2>
      </el-col>
    </el-row>
  </span>
</template>
<script>
const API_URL = "https://api.sustainability.oregonstate.edu/v2/energy";

export default {
  components:{},
  data: () => ({
    current: {}, // reading during campaign timeline
    baseline: {}, // baseline
    difference: {}, // % difference
    meterGroupMetadata: {},
    buildings: [],
    MGBuildingMap: {},
    activeCampaign: null,
    loaded: false,
    status: "Fetching OSU building data...",
    styleC: {
      display: "inline-block",
      width: "98%",
      height: "340px",
      "padding-right": "0.5em",
      "padding-left": "0.5em",
      "padding-top": "1em",
    },
    chartOptions: {
      elements: {
        point: {
          radius: 3,
        },
      },
    },
  }),
  created() {
    this.setupCampaign();
  },
  computed: {
    chartData() {
      const cd = { datasets: [] };
      for (let meterGroup of Object.keys(this.difference)) {
        cd.datasets.push({
          label: `${meterGroup}`,
          data: this.difference[meterGroup],
        });
      }
      return cd;
    },
  },
  methods: {
    async setupCampaign() {
      // fetch building & campaign data from API
      const buildingData = await this.callAPI("allbuildings");
      this.status = "Fetching campaign data...";
      const campaigns = await this.callAPI("campaigns");
      // just keep the meter group -> meter mappings
      for (let building of buildingData) {
        for (let mg of building.meterGroups) {
          this.meterGroupMetadata[mg.id] = mg.meters;
          this.MGBuildingMap[mg.id] = building.name;
        }
      }
      this.activeCampaign = campaigns[campaigns.length - 1];

      // Need to do a little date normalization to make UTC time PST time.
      const dateStart = Math.floor(
        (new Date(this.activeCampaign.dateStart).getTime() -
          new Date().getTimezoneOffset() * 60 * 1000) /
          1000
      );
      const dateEnd = Math.floor(
        (new Date(this.activeCampaign.dateEnd).getTime() -
          new Date().getTimezoneOffset() * 60 * 1000) /
          1000
      );
      const compareStart = Math.floor(
        new Date(this.activeCampaign.compareStart).getTime() / 1000
      );
      const compareEnd = Math.floor(
        new Date(this.activeCampaign.compareEnd).getTime() / 1000
      );

      let count = 1;
      this.status = `Collecting data for ${count}/${this.activeCampaign.meterGroupIDs.length} buildings...`;
      for (let meterGroup of this.activeCampaign.meterGroupIDs) {
        // 1. find meters referenced in campaign meter-group
        for (let meter of this.meterGroupMetadata[meterGroup]) {
          // 2. get baseline data
          const baseline = await this.callAPI(
            `data/?id=${meter.id}&startDate=${compareStart}&endDate=${compareEnd}&point=accumulated_real&meterClass=${meter.classInt}`
          );
          // 3. get current data
          const current = await this.callAPI(
            `data/?id=${meter.id}&startDate=${dateStart}&endDate=${dateEnd}&point=accumulated_real&meterClass=${meter.classInt}`
          );
          // 4. store data sets
          
          // compute net totals for overall %s
          let currentTotal = 0
          let baselineTotal = 0

          this.baseline[meter.id] = []
          // chart data map building meter group -> % difference
          for (let i = 0; i < baseline.length-1; i++) {
            let change = baseline[i].accumulated_real - baseline[i+1].accumulated_real
            if (isNaN(change)) continue
            
            this.baseline[meter.id].push(change)
          }

          for (let i = 0; i < current.length-1; i++) {
            let change = current[i].accumulated_real - current[i+1].accumulated_real
            if (isNaN(change) || isNaN(baselineTotal + this.baseline[meter.id][i % baseline.length]))
              continue
            currentTotal += change
            baselineTotal += this.baseline[meter.id][i % baseline.length]
          }
          // push building & total progress

          this.buildings.push({
            name: this.MGBuildingMap[meterGroup], 
            percent: (100*((currentTotal-baselineTotal)/baselineTotal)).toFixed(2),
            baseline: `${baselineTotal} kwh`,
            current: `${currentTotal} kwh`
          });

          count++;
          this.status = `Collecting data for ${count}/${this.activeCampaign.meterGroupIDs.length} buildings...`;
        }
      }

      this.buildings.sort((a,b) => a.percent-b.percent)
      for (let i = 0; i < this.buildings.length; i++){
        this.buildings[i].rank = i+1;
        let addPlus = this.buildings[i].percent > 0;
        this.buildings[i].percent = `${this.buildings[i].percent}%`;
        if (addPlus) {
          this.buildings[i].percent = '+'+this.buildings[i].percent;
        }
        
      }
      this.loaded = true;
    },
    // just hits API endpoint.
    async callAPI(route) {
      return await (await fetch(`${API_URL}/${route}`)).json();
    },
  },
};
</script>
